def get_quote(bot_name):
    """–í–æ–∑–≤—Ä–∞—â–∞–µ—Ç —Å–ª—É—á–∞–π–Ω—É—é —Ü–∏—Ç–∞—Ç—É –¥–ª—è –±–æ—Ç–∞"""
    import random
    if "Daniel" in bot_name or "–¥–∞–Ω–∏" in bot_name.lower():
        return random.choice(DANIEL_QUOTES)
    elif "Leonardo" in bot_name or "–ª–µ–æ–Ω–∞—Ä–¥–æ" in bot_name.lower():
        return random.choice(LEONARDO_QUOTES)
    elif "–ê–ª–µ–≤—Ç–∏–Ω–∞" in bot_name or "alevtina" in bot_name.lower():
        return random.choice(ALEVTINA_QUOTES)
    else:
        return random.choice(LEONARDO_QUOTES)

